#include <stdio.h>
#include <string.h>

int main()
{
    char *DNAString = "";
    int length = strlen(DNAString);

    int a = 0, c = 0, g = 0, t = 0;

    for (int i = 0; i < length; ++i)
    {
        switch (DNAString[i])
        {
            case 'A': a++; break;
            case 'C': c++; break;
            case 'G': g++; break;
            case 'T': t++; break;
        }
    }

    printf("The number of nucleotides in \"A C G T\" format: %d %d %d %d\n", a, c, g, t);

    return 0;
}